        }"
      />
    </section>

    <div class="gradient-box-bottom"></div>
  </main>
</template>

<script setup>
// imports
import RewardScore from "@/components/mainPage/RewardScore.vue";
// import Airdrop from "@/components/base/Airdrop.vue";
import { ref, watchEffect, onMounted, onUnmounted, nextTick } from "vue";
import { useLanguageStore } from "@/stores/languageStore";
import { useAuthStore } from "@/stores/authStore";
import LeaderCup from "@/components/rating/LeaderCup.vue";
import { useBalanceStore } from "@/stores/balance";
import { usePowersStore } from "@/stores/powers";
import { useFriendList } from "@/stores/friendList";
import { useCurtainStore } from "@/stores/curtain";
import Tooltip from "@/components/mainPage/Tooltip.vue";
import { shouldStartOnboarding, completeInstruction } from "@/utils/data";
import introJs from "intro.js";
import "intro.js/minified/introjs.min.css";
import { useRouter } from "vue-router";
// stores
const balanceStore = useBalanceStore();
const powersStore = usePowersStore();
const friendList = useFriendList();
const languageStore = useLanguageStore();
const curtainStore = useCurtainStore();
const authStore = useAuthStore();
const router = useRouter();

// variables
const skeleton = ref(true);
const friends = ref(0);
const mainOnboardingSkip = ref(false);

const powers = ref({
  deviceCounter: 0,
  totalCpu: 0,
  totalRam: 0,
  totalHdd: 0,
  totalGpu: 0,
  totalCpuIndex: 0,
  totalRamIndex: 0,
  totalDiskIndex: 0,
  totalGpuIndex: 0,
  updatedTimestamp: 1740247170,
});

let skeletonTimeout = null;
let introStarted = ref(false);

// Функция для управления скроллом во время онбординга
const preventScrollDuringOnboarding = () => {
  if (introStarted.value) {
    document.body.classList.add('onboarding-active');
    document.body.style.overflow = 'hidden';
  } else {
    document.body.classList.remove('onboarding-active');
    document.body.style.overflow = '';
  }
};

// Функция для запуска онбординга
async function startOnboarding() {
  if (introStarted.value) return;

  // Сбрасываем флаг skip при запуске нового онбординга
  mainOnboardingSkip.value = false;
  introStarted.value = true;
  
  // СПЕРВА скролл к верху, ПОТОМ блокировка скролла и онбординг
  document.getElementById('app')?.scrollTo({ top: 0, behavior: 'smooth' });
  
  // Ждем завершения скролла, затем запускаем онбординг
  await new Promise(resolve => setTimeout(resolve, 600)); // Ждем завершения анимации скролла
  
  // Блокируем скролл во время онбординга
  const appElement = document.getElementById('app');
  if (appElement) {
    appElement.style.overflow = 'hidden';
  }
  // Дополнительно блокируем скролл для body
  document.body.style.overflow = 'hidden';
  document.documentElement.style.overflow = 'hidden';
  document.body.classList.add('onboarding-active');

  // Небольшая задержка для правильной инициализации DOM
  setTimeout(() => {

    const tour = typeof introJs.tour === 'function' ? introJs.tour() : introJs();
    // Подключаем наблюдатель: вешаем класс и переносим skip внутрь .introjs-tooltipbuttons
    let skipObserver = null;
    const attachSkipClassWatcher = () => {
      const tagAndMove = () => {
        document.querySelectorAll('.introjs-skipbutton').forEach((btn) => {
          // Класс для стилизации
          btn.classList.add('hn-skip');
          // Переносим кнопку внутрь панели кнопок текущего тултипа
          const tooltip = btn.closest('.introjs-tooltip');
          const buttonsBar = tooltip ? tooltip.querySelector('.introjs-tooltipbuttons') : null;
          if (buttonsBar && btn.parentElement !== buttonsBar) {
            try { buttonsBar.insertBefore(btn, buttonsBar.firstChild || null); } catch (_) {}
          }
          // Принудительно перехватываем клик по Skip ПЕРЕД обработкой Intro.js
          if (!btn.dataset.hnBound && btn.classList.contains('introjs-skipbutton')) {
            btn.addEventListener('click', (e) => {
              // Останавливаем всплытие событий к Intro.js только для кнопки Skip
              e.preventDefault();
              e.stopPropagation();
              e.stopImmediatePropagation();
              
              // Помечаем что это Skip
              mainOnboardingSkip.value = true;
              
              // Принудительно вызываем exit вместо complete
              setTimeout(() => {
                try { tour.exit(); } catch (_) {}
              }, 0);
              
              return false;
            }, true); // Фаза захвата для приоритета перед Intro.js
            btn.dataset.hnBound = '1';
          }
        });
      };
      // Мгновенно пометить/перенести, если кнопка уже есть
      tagAndMove();
      // Дальше отслеживаем появление/перерисовку
      skipObserver = new MutationObserver(() => tagAndMove());
      skipObserver.observe(document.body, { childList: true, subtree: true });
    };

    tour.setOptions({
        exitOnEsc: false,
        exitOnOverlayClick: false,
        showStepNumbers: false,
        showBullets: false,
        showProgress: true,
        scrollToElement: true,
        scrollTo: "element",
        scrollPadding: 30,
        overlayOpacity: 0.75,
        tooltipPosition: "auto",
        positionPrecedence: ["bottom", "top", "right", "left"],
        disableInteraction: true,
        hidePrev: true,
        hideNext: false,
        nextLabel: "→",
        prevLabel: "← Назад",
        skipLabel: "Пропустить",
        doneLabel: "→",
        showSkipButton: false,
        helperElementPadding: 10,
        highlightClass: "introjs-highlight",
        steps: [
          {
            element: "#balance-details",
            intro: languageStore.t("tutorial.mainPage.balanceDetails.intro"),
            title: languageStore.t("tutorial.mainPage.balanceDetails.title"),
            position: "bottom",
          },
          {
            element: "#powers-title",
            intro: languageStore.t("tutorial.mainPage.powersMonitoring.intro"),
            title: languageStore.t("tutorial.mainPage.powersMonitoring.title"),
            position: "bottom",
          },
        ],
      });
    tour.onafterchange(() => {
      if (!skipObserver) attachSkipClassWatcher();
      // Дублируем перенос с микрозадержкой — на случай, если DOM дорисовывается после события
      setTimeout(() => {
        try {
          const skipBtn = document.querySelector('.introjs-skipbutton');
          const tooltip = skipBtn ? skipBtn.closest('.introjs-tooltip') : null;
          const buttonsBar = tooltip ? tooltip.querySelector('.introjs-tooltipbuttons') : null;
          if (skipBtn && buttonsBar && skipBtn.parentElement !== buttonsBar) {
            buttonsBar.insertBefore(skipBtn, buttonsBar.firstChild || null);
          }
        } catch (_) {}
      }, 0);
    });
    tour.oncomplete(() => {
        if (mainOnboardingSkip.value) {
          // Если до этого нажали Skip (onexit), не выполняем сценарий завершения с редиректом
          return;
        }
        // Сохраняем что онбординг главной страницы пройден
        localStorage.setItem("TMA-onboarding-main-completed", "true");
        introStarted.value = false;
      if (skipObserver) { skipObserver.disconnect(); skipObserver = null; }
        // Разблокируем скролл
        const appElement = document.getElementById('app');
        if (appElement) {
          appElement.style.overflow = 'auto';
        }
        document.body.style.overflow = '';
        document.documentElement.style.overflow = '';
        document.body.classList.remove('onboarding-active');
        // Переходим на страницу устройств
        router.push("/devices");
      });
    tour.onexit(async () => {
        // Проверяем, что это действительно Skip, а не случайный exit
        if (!mainOnboardingSkip.value) {
          // Если это не Skip, просто выходим без дополнительных действий
          return;
        }
        
        // Пропустить: полностью завершаем онбординг локально и на сервере, без перехода
        localStorage.setItem('TMA-onboarding-main-completed', 'true');
        localStorage.setItem('TMA-onboarding-devices-completed', 'true');
        localStorage.setItem('TMA-onboarding-devices-state', 'completed');
        localStorage.setItem('TMA-onboarding-invite-state', 'completed');
        try { await completeInstruction(); } catch (_) {}

        introStarted.value = false;
        if (skipObserver) { skipObserver.disconnect(); skipObserver = null; }

        // Разблокируем скролл и чистим артефакты интро
        const appElement = document.getElementById('app');
        if (appElement) { appElement.style.overflow = 'auto'; }
        document.body.style.overflow = '';
        document.documentElement.style.overflow = '';
        document.body.classList.remove('onboarding-active');
        try { (typeof introJs.tour === 'function' ? introJs.tour() : introJs()).exit(); } catch (_) {}
      });
    tour.start();
  }, 100); // Задержка 100ms для инициализации
}

// Глобальная кнопка «Пропустить»: завершает весь онбординг и на сервере
async function skipAllOnboarding() {
  try { introJs.tour().exit(); } catch (_) {}
  localStorage.setItem('TMA-onboarding-main-completed', 'true');
  localStorage.setItem('TMA-onboarding-devices-completed', 'true');
  localStorage.setItem('TMA-onboarding-devices-state', 'completed');
  localStorage.setItem('TMA-onboarding-invite-state', 'completed');
  introStarted.value = false;
  const appElement = document.getElementById('app');
  if (appElement) appElement.style.overflow = 'auto';
  document.body.style.overflow = '';
  document.documentElement.style.overflow = '';
  document.body.classList.remove('onboarding-active');
  try { await completeInstruction(); } catch (_) {}
}

// Функция для тестирования онбординга (сбрасывает localStorage)
function testOnboarding() {
  localStorage.removeItem("TMA-onboarding-main-completed");
  // startOnboarding(); // disabled
}

function resetOnboarding() {
  localStorage.removeItem("TMA-onboarding-main-completed");
  localStorage.removeItem("TMA-onboarding-devices-completed");
  localStorage.removeItem("TMA-onboarding-devices-state");
  localStorage.removeItem("TMA-onboarding-invite-state");
}

// Экспортируем функцию для возможности запуска по требованию
defineExpose({
  startOnboarding,
  testOnboarding,
});

watchEffect(() => {
  const isDataLoaded =
    balanceStore.balanceModel &&
    powersStore.powers &&
    friendList.friendCount != null;
  if (isDataLoaded) {
    friends.value = friendList.friendCount;
    powers.value = powersStore.powers;

    if (skeletonTimeout) {
      clearTimeout(skeletonTimeout);
    }

    skeletonTimeout = setTimeout(() => {
      skeleton.value = false;

      // Запускаем онбординг когда данные загружены и скелетон скрыт
      setTimeout(() => {
        if (shouldStartOnboarding("TMA-onboarding-main-completed")) {
          // startOnboarding(); // disabled
        }
      }, 1000);
    }, 500);
  } else {
    // Если данные не загружены, сбрасываем таймаут и показываем скелетон
    if (skeletonTimeout) {
      clearTimeout(skeletonTimeout);
    }
    skeleton.value = true;
  }
});

onUnmounted(() => {
  if (skeletonTimeout) {
    clearTimeout(skeletonTimeout);
  }
  
  // Разблокируем скролл при размонтировании компонента
  document.body.classList.remove('onboarding-active');
  document.body.style.overflow = '';
});

function toggleCurtain() {
  curtainStore.setCustomProp("reward");
  // curtainStore.setCustomProp("policy");
  curtainStore.toggleCurtain();
}

function handlePointerDown(event) {
  event.preventDefault();
}

function handlePointerUp(event) {
  event.preventDefault();
}

onMounted(() => {});

const tooltip = ref({
  show: false,
  text: "",
  x: 0,
  y: 0,
});

function showTooltip(key, event) {
  tooltip.value.text = languageStore.t("mainPage.tooltips." + key);
  tooltip.value.show = true;
  nextTick(() => {
    const container = event.currentTarget.closest(".characters");
    const rect = event.currentTarget.getBoundingClientRect();
    const containerRect = container.getBoundingClientRect();
    const elementHeight = rect.height;
    tooltip.value.x = rect.left + rect.width / 2 - containerRect.left;
    tooltip.value.y = rect.top - containerRect.top + elementHeight + 10; // 10px — отступ для стрелочки
  });
}

function hideTooltip() {
  tooltip.value.show = false;
}

function handleClickOutside(e) {
  if (tooltip.value.show) {
    hideTooltip();
  }
}

document.addEventListener("click", handleClickOutside);
onUnmounted(() => {
  document.removeEventListener("click", handleClickOutside);
});
</script>

